mapper.delete:

delete from role_resource
where ROLE_ID = #{roleId,jdbcType=VARCHAR}
  and RESOURCE_ID = #{resourceId,jdbcType=VARCHAR}

mapper.insert:

insert into role_resource (ROLE_ID, RESOURCE_ID, crt_user, 
  crt_time, lst_upd_user, lst_upd_time, 
  lst_upd_date, rec_status, scr_level
  )
values (#{roleId,jdbcType=VARCHAR}, #{resourceId,jdbcType=VARCHAR}, #{crtUser,jdbcType=VARCHAR}, 
  #{crtTime,jdbcType=TIMESTAMP}, #{lstUpdUser,jdbcType=VARCHAR}, #{lstUpdTime,jdbcType=TIMESTAMP}, 
  #{lstUpdDate,jdbcType=DATE}, #{recStatus,jdbcType=CHAR}, #{scrLevel,jdbcType=CHAR}
  )

mapper.update:

update role_resource
set crt_user = #{crtUser,jdbcType=VARCHAR},
  crt_time = #{crtTime,jdbcType=TIMESTAMP},
  lst_upd_user = #{lstUpdUser,jdbcType=VARCHAR},
  lst_upd_time = #{lstUpdTime,jdbcType=TIMESTAMP},
  lst_upd_date = #{lstUpdDate,jdbcType=DATE},
  rec_status = #{recStatus,jdbcType=CHAR},
  scr_level = #{scrLevel,jdbcType=CHAR}
where ROLE_ID = #{roleId,jdbcType=VARCHAR}
  and RESOURCE_ID = #{resourceId,jdbcType=VARCHAR}

mapper.select:

select ROLE_ID, RESOURCE_ID, crt_user, crt_time, lst_upd_user, lst_upd_time, lst_upd_date, 
rec_status, scr_level
from role_resource
where ROLE_ID = #{roleId,jdbcType=VARCHAR}
  and RESOURCE_ID = #{resourceId,jdbcType=VARCHAR}
